import React, { Component } from "react";
import TaskList from "../task-list";
import NewTaskForm from "../new-task-form";
import Footer from "../footer";
import { formatDistanceToNow } from "date-fns";

// react -> render
// setTimeout(1000, reRender)

export default class App extends Component {
  maxId = 100;

  state = {
    todoData: [
      this.createTask("Completed Task"),
      this.createTask("Editing Task"),
      this.createTask("Active Task"),
    ],
    filter: "All",
    createTime: this.tick(),
    // isMounted: false,
  };

  // componentDidMount() {
  //   this._isMounted = true;
  //   this.timerID = this.tick();
  // }

  // componentWillUnmount() {
  //   this._isMounted = false;
  //   clearInterval(this.timerID);
  // }

  tick() {
    // if (this._isMounted) {
    const created = new Date();
    this.createTime = formatDistanceToNow(created, {
      includeSeconds: true,
    });

    return this.createTime;
    // }
  }

  onFilterChange = (filter) => {
    this.setState({ filter });
  };

  clearTasks = () => {
    this.setState(({ todoData }) => {
      const newArr = [];
      for (let i = 0; i < todoData.length; i++) {
        if (!todoData[i].completed) {
          newArr.push(todoData[i]);
        }
      }
      return {
        todoData: newArr,
      };
    });
  };

  filterTasks(todoData, filter) {
    if (filter === "All") return todoData;
    else if (filter === "Active") {
      return todoData.filter((task) => !task.completed);
    } else if (filter === "Completed") {
      return todoData.filter((task) => task.completed);
    }
  }

  createTask(label, createTime = this.tick()) {
    console.log(this.createTime);
    // label, creationDateTime
    return {
      label,
      completed: false,
      id: this.maxId++,
      createTime: this.createTime,
    };
  }

  deleteTask = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);

      const newArray = [...todoData.slice(0, idx), ...todoData.slice(idx + 1)];
      return {
        todoData: newArray,
      };
    });
  };

  /**
   * Root
   *   - textInput <- AddTask(a,b)
   *       | props.textInput(a,b)
   *
   */

  addTask = (text) => {
    console.log(this.state.todoData);
    // addTask = ({label, createionDateTime}) => {
    const newTask = this.createTask(text);
    this.setState(({ todoData }) => {
      const newArr = [...todoData, newTask];
      return {
        todoData: newArr,
      };
    });
  };

  onToggleDone = (id) => {
    this.setState(({ todoData }) => {
      const idx = todoData.findIndex((el) => el.id === id);
      const oldTask = todoData[idx];
      const newTask = { ...oldTask, completed: !oldTask.completed };
      const newArray = [
        ...todoData.slice(0, idx),
        newTask,
        ...todoData.slice(idx + 1),
      ];
      return {
        todoData: newArray,
      };
    });
  };

  render() {
    const { todoData, filter, createTime } = this.state;
    const visibleTasks = this.filterTasks(todoData, filter);
    const completedCount = todoData.filter((el) => el.completed).length;
    const leftTasksCount = todoData.length - completedCount;

    return (
      <div className="todoapp">
        <NewTaskForm addTask={this.addTask} createTime={createTime} />
        <section className="main">
          <TaskList
            todos={visibleTasks}
            onDeleted={this.deleteTask}
            onToggleDone={this.onToggleDone}
            createTime={setInterval(this.tick(), 2000)}
          />
          <Footer
            leftTasksCount={leftTasksCount}
            filter={filter}
            onFilterChange={this.onFilterChange}
            clearTasks={this.clearTasks}
          />
        </section>
      </div>
    );
  }
}
